import styled from 'styled-components';

export const ListComponent = styled.div`

  margin: 2px;
  .list-container {
    display: flex;
    align-items: center;
    justify-content: center;
    border: 1px solid #ececec;
    padding: 1px;
    border-radius: 4px;
    position:relative;

  margin-bottom:10px;
  .list-image {
    height: 100px;
    width: 100px;
    position: relative;
    background-position: center;
    background-size: 100%;
    border-radius: 5px;
  }
  .list-details {
    width: calc(100% - 100px);
    padding-left: 14px;
    .time {
      background: green;
      color: #fff;
      padding: 1px 10px;
      border-radius: 4px;
      position: absolute;
      right: 0;
      bottom: 2px;
      width: 40px;
      text-align: center;
      &.closed{
        background: red;
      }
    }
  }
}
`;
