import { createStore, applyMiddleware, compose  } from 'redux';
import thunk from 'redux-thunk';
import rootReducer from './reducer';


const middleWares = [
    thunk,
    routerMiddleware(BrowserRouter)]
const store = createStore(
	rootReducer,	
	compose(
        applyMiddleware(
            ...middleWares
        )
    )
)



export default store;
