import styled from 'styled-components';

export const ListComponent = styled.div`

  margin: 2px;
  .list-container {
    display: flex;
    align-items: center;
    justify-content: center;
.list-container{
  margin-bottom:10px;
}
  .list-image {
    height: 100px;
    width: 100px;
    position: relative;
    background-position: center;
    background-size: 100%;
    border-radius: 5px;
  }
  .list-details {
    width: calc(100% - 100px);
    padding-left: 14px;
  }
}
`;
