import React,{Component} from 'react';
import noImage from '../no_preview.jpg'

class List extends Component {
  render(){
    let { details ,isOpen} = this.props;
    let image = details.photos && details.photos.length ? details.photos[0].getUrl():noImage
    return(
      <div key={details.id} className="list-container">
        <div class="list-image" style={{backgroundImage: `url(${image})`}} />
        <div className="list-details">
          <h4>{details.name}</h4>
          <span className="address">{details.vicinity}</span>
          <span class={`time ${isOpen ? 'open' : 'closed'}`}>{isOpen ? 'Open' : 'Closed'}</span>
        </div>
      </div>
    );
  }
}
export default List;
