import React, { Component } from "react";
import { connect } from "react-redux";
import Helper from "../utils/Helper";
import { strings } from "../utils/strings"

class PlaceType extends Component {
  state = {
    selectedList: [],
    types:this.props.types || []
  };

  componentWillReceiveProps(nextProps){
    this.setState({
      types : nextProps.types
    })
  }  

  handleInputChange = event => {
    const target = event.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.value;
    let _type = this.state.selectedList;
    if (_type.includes(name)) {
      let index = _type.indexOf(name);
      _type.splice(index, 1);
    } else {
      _type.push(name);
    }
    this.setState(
      {
        selectedList: _type
      },
      () => {
        this.props.selectedPlace(_type);
      }
    );
  };

  handleFindString = e => {
    let _value = e.target.value;
    let { types, initialTypes } = this.props;
    _value = _value.toLowerCase();
    this.setState({ value: _value });
    if (_value === "") {
      this.setState({ types: initialTypes });
      return;
    }
    const regex = new RegExp(_value);
    let newTypes = this.props.types.filter(element =>
      regex.test(element, _value)
    );
    this.setState({ types: newTypes });
  };

  render() {
    let { types } = this.props;
    return (
      <div className="types--container">
        <div className="types--searchbox">
          <input
            type="text"
            placeholder={strings.SearchPlaceHolder}
            onChange={e => this.handleFindString(e)}
          />
          <span>{strings.NearMe}</span>
        </div>
        {types.map((item, index) => {
          item = Helper.replaceDash(item);
          return (
            <label key={index}>
              <input
                type="checkbox"
                className="types"
                value={item}
                onChange={this.handleInputChange}
              />
              {Helper.capitalizeFirstLetter(item)}
            </label>
          );
        })}
      </div>
    );
  }

}

const mapStateToProps = state => ({
  ...state
});

export default connect(mapStateToProps, {})(PlaceType);
