import styled from 'styled-components';

export const ListContainerStyle = styled.div`
    height:calc(100vh - 300px);
    overflow:auto;
    margin: 2px;
    font-family: 'Open Sans', sans-serif;
  .nearest-list--container {
    display: flex;
    align-items: center;
    justify-content: center;
    border: 1px solid #ececec;
    padding: 1px;
    border-radius: 4px;
    position:relative;
    padding-right:75px;
  margin-bottom:10px;
  .nearest-list--image {
    height: 100px;
    width: 100px;
    position: relative;
    background-position: center;
    background-size: 100%;
    border-radius: 5px;
  }
  .nearest-list--details {
    width: calc(100% - 100px);
    padding-left: 14px;
    .nearest-list--time {
      background: green;
      color: #fff;
      padding: 1px 10px;
      border-radius: 4px;
      position: absolute;
      right: 0;
      bottom: 2px;
      width: 50px;
      text-align: center;
      &.closed{
        background: red;
      }
    }
  }
}
`;
