import React,{Component} from 'react';
import noImage from '../no_preview.jpg'
import {InfoContainerStyle} from '../styles/InfoContainer.style'

class PlaceType extends Component {
  capitalizeFirstLetter(string) {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }
  handleInputChange=(event)=> {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.value;
    console.log(value,name)
    this.setState({
      [name]: value
    });
  }

  render(){
    const   types = ['accounting','airport','amusement_park','aquarium','art_gallery','atm','bakery','bank','bar','beauty_salon','bicycle_store','book_store','bowling_alley','bus_station','cafe','campground','car_dealer','car_rental','car_repair','car_wash','casino','cemetery','church','city_hall','clothing_store','convenience_store','courthouse','dentist','department_store','doctor','electrician','electronics_store','embassy','fire_station','florist','funeral_home','furniture_store','gas_station','gym','hair_care','hardware_store','hindu_temple','home_goods_store','hospital','insurance_agency','jewelry_store','laundry','lawyer','library','liquor_store','local_government_office','locksmith','lodging','mel_delivery','meal_takeaway','mosque','movie_rental','movie_theater','moving_company','museum','night_club','painter','park','parking','pet_store','pharmacy','physiotherapist','plumber','police','post_office','real_estate_agency','restaurant','roofing_contractor','rv_park','school','shoe_store','shopping_mall','spa','stadium','storage','store','subway_station','synagogue','taxi_stand','train_station','transit_station','travel_agency','university','veterinary_care','zoo'];
    return(
      <div  className="types--container">
        {types.map((item,index)=>{
         item = item.replace(/_/g, " ");
          return(
            <label key={index}>
              <input type="checkbox" class="types" value={item} onChange={this.handleInputChange} />{this.capitalizeFirstLetter(item)}
            </label>
          );
        })}

      </div>
    );
  }
}
export default PlaceType;
