import React,{Component} from 'react';
import noImage from '../no_preview.jpg'

class PlaceType extends Component {
  state = {
    types : ['accounting','airport','amusement_park','aquarium','art_gallery','atm','bakery','bank','bar','beauty_salon','bicycle_store','book_store','bowling_alley','bus_station','cafe','campground','car_dealer','car_rental','car_repair','car_wash','casino','cemetery','church','city_hall','clothing_store','convenience_store','courthouse','dentist','department_store','doctor','electrician','electronics_store','embassy','fire_station','florist','funeral_home','furniture_store','gas_station','gym','hair_care','hardware_store','hindu_temple','home_goods_store','hospital','insurance_agency','jewelry_store','laundry','lawyer','library','liquor_store','local_government_office','locksmith','lodging','mel_delivery','meal_takeaway','mosque','movie_rental','movie_theater','moving_company','museum','night_club','painter','park','parking','pet_store','pharmacy','physiotherapist','plumber','police','post_office','real_estate_agency','restaurant','roofing_contractor','rv_park','school','shoe_store','shopping_mall','spa','stadium','storage','store','subway_station','synagogue','taxi_stand','train_station','transit_station','travel_agency','university','veterinary_care','zoo'],
    initialTypes :  ['accounting','airport','amusement_park','aquarium','art_gallery','atm','bakery','bank','bar','beauty_salon','bicycle_store','book_store','bowling_alley','bus_station','cafe','campground','car_dealer','car_rental','car_repair','car_wash','casino','cemetery','church','city_hall','clothing_store','convenience_store','courthouse','dentist','department_store','doctor','electrician','electronics_store','embassy','fire_station','florist','funeral_home','furniture_store','gas_station','gym','hair_care','hardware_store','hindu_temple','home_goods_store','hospital','insurance_agency','jewelry_store','laundry','lawyer','library','liquor_store','local_government_office','locksmith','lodging','mel_delivery','meal_takeaway','mosque','movie_rental','movie_theater','moving_company','museum','night_club','painter','park','parking','pet_store','pharmacy','physiotherapist','plumber','police','post_office','real_estate_agency','restaurant','roofing_contractor','rv_park','school','shoe_store','shopping_mall','spa','stadium','storage','store','subway_station','synagogue','taxi_stand','train_station','transit_station','travel_agency','university','veterinary_care','zoo']
  }
  capitalizeFirstLetter(string) {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }
  handleInputChange=(event)=> {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.value;
    let _type = this.state.type;
    if(_type.includes(name)){
      let index = _type.indexOf(name);
      _type.splice(index,1);
    } else {
      _type.push(name);
    }
    this.setState({
      type: _type
    },()=>{
      this.props.selectedPlace(_type);
    });
  }
  handleFindString = e => {
    let _value = e.target.value;
    _value = _value.toLowerCase();
    this.setState({ value: _value });
    if (_value === "") {
      this.setState({ types: this.state.initialTypes });
      return;
    }
    const regex = new RegExp(e.target.value);
    let NewArr = this.state.types.filter(element =>
      regex.test(element, e.target.value)
    );
    this.setState({ currentList: NewArr });
  };
  render(){

    return(
      <div  className="types--container">
        <div className="types--searchbox">
          <input type="text" placeholder="Place"  onChange={e => this.handleFindString(e)}/>
            <span>Near me</span>
        </div>
        {types.map((item,index)=>{
         item = item.replace(/_/g, " ");
          return(
            <label key={index}>
              <input type="checkbox" className="types" value={item} onChange={this.handleInputChange} />{this.capitalizeFirstLetter(item)}
            </label>
          );
        })}

      </div>
    );
  }
}
export default PlaceType;
