import React, { Component } from "react";
import { connect } from "react-redux";
import Helper from "../utils/Helper";
import { strings } from "../utils/strings"

class PlaceType extends Component {
  state = {
    initialTypes: [
      "accounting",
      "airport",
      "amusement_park",
      "aquarium",
      "art_gallery",
      "atm",
      "bakery",
      "bank",
      "bar",
      "beauty_salon",
      "bicycle_store",
      "book_store",
      "bowling_alley",
