import React,{Component} from 'react';
import noImage from '../no_preview.jpg'
import {InfoContainerStyle} from '../styles/InfoContainer.style'

class InfoComponent extends Component {
  render(){
    let { details} = this.props;
    console.log("details",details,list)
    let image = details.photos && details.photos.length ? details.photos[0].getUrl():noImage
    return(
      <InfoContainerStyle key={details.id} className="infowindow--container">
        <div class="infowindow--image" style={{backgroundImage: `url(${image})`}} />
        <div className="infowindow--details">
          <h4>{details.name}</h4>
          <span className="infowindow--address">{details.vicinity}</span>
          <span class={`infowindow--time ${details.openning_hours && details.openning_hours.open_now ? 'open' : 'closed'}`}>{details.openning_hours && details.openning_hours.open_now ? 'Open' : 'Closed'}</span>
        </div>
      </InfoContainerStyle>
    );
  }
}
export default InfoComponent;
